import React from "react";
import Header from "../Components/Header";

const Home = () => {
  document.addEventListener("DOMContentLoaded", () => {
    let parent = document.querySelector(".splitview"),
      topPanel = parent.querySelector(".top"),
      handle = parent.querySelector(".handle"),
      skewHack = 0,
      delta = 0;

    if (parent.className.indexOf("skewed") != -1) {
      skewHack = 1000;
    }
    parent.addEventListener("mousemove", (event) => {
      delta = (event.clientX - window.innerWidth / 2) * 0.5;
      handle.getElementsByClassName.left = event.clientX + delta + "px";
      topPanel.getElementsByClassName.width =
        event.clientX + skewHack + delta + "px";
    });
  });
  return (
    <>
      <main>
        <Header />
        <section className="welcome">
          <h2>
            Find the perfect <strong>design</strong>, <br />
            We find the perfect <span>artist</span>.
          </h2>
        </section>
        <section className="splitview skewed">
          <div className="panel bottom">
            <div className="content">
              <div className="description">
                <h2>Title</h2>
                <p>Lorem ipsum dolor sit amet consectetur adipisicing elit.</p>
              </div>
              <div className="OriginalPicture"></div>
            </div>
          </div>
          <div className="panel top">
            <div className="content">
              <div className="description">
                <h2>Title</h2>
                <p>Lorem ipsum dolor sit, amet consectetur adipisicing elit.</p>
              </div>
              <div className="afterPicture"></div>
            </div>
          </div>
          <div className="handle"></div>
        </section>
      </main>
    </>
  );
};

export default Home;
